mapper.select:

where




                d1.approval_status = #{documentQueryParam.approvalStatus}
                AND d1.publish_status = #{documentQueryParam.publishStatus}
                AND d1.id IN (SELECT
                max(d3.id) FROM
                (
                SELECT d5.id, d5.item_no, d5.material_no, d5.document_type, d5.sequence_no FROM document d5
                INNER JOIN user u3 ON d5.user_id = u3.id LEFT JOIN user u4 ON d5.sub_id = u4.id
                WHERE d5.approval_status = 1 AND d5.publish_status = 1
                ) d3
                GROUP BY d3.item_no, d3.material_no, d3.document_type, d3.sequence_no
                )


                d1.id in (
                select max(d2.id)
                from document d2
                group by d2.item_no, d2.material_no, d2.document_type, d2.sequence_no
                )






        and d1.create_time >= #{documentQueryParam.createTimeStart}



        and d1.create_time <= #{documentQueryParam.createTimeEnd}



        and d1.publish_time >= #{documentQueryParam.publishTimeStart}



        and d1.publish_time <= #{documentQueryParam.publishTimeEnd}


        and d1.item_no like CONCAT('%',#{documentQueryParam.itemNo}, '%')


        and d1.material_no like CONCAT('%',#{documentQueryParam.materialNo}, '%')


        and d1.depart_id  = #{documentQueryParam.departId}


        and d1.approval_status  = #{documentQueryParam.approvalStatus}


        and d1.publish_status  = #{documentQueryParam.publishStatus}


        and u1.id = #{documentQueryParam.userId}


        and d1.document_type in

            #{item}






                ORDER BY d1.create_time ${documentQueryParam.order}


                ORDER BY d1.update_time ${documentQueryParam.order}


                ORDER BY d1.approval_time ${documentQueryParam.order}


                ORDER BY d1.publish_time ${documentQueryParam.order}


                ORDER BY d1.deleted ${documentQueryParam.order}




                ORDER BY d1.id DESC


WHERE d1.item_no = #{document.itemNo}
AND d1.sequence_no = #{document.sequenceNo}
AND d1.document_type = #{document.documentType}
AND d1.document_version <> #{document.documentVersion}

    AND d1.material_no IS NULL


    AND d1.material_no = #{document.materialNo}

where d1.approval_status = 1 AND d1.publish_status = 1 AND d1.id IN (SELECT max(d3.id) FROM ( SELECT d5.id, d5.item_no, d5.material_no, d5.document_type, d5.sequence_no FROM document d5 INNER JOIN user u3 ON d5.user_id = u3.id LEFT JOIN user u4 ON d5.sub_id = u4.id WHERE d5.approval_status = 1 AND d5.publish_status = 1 ) d3 GROUP BY d3.item_no, d3.material_no, d3.document_type, d3.sequence_no )
and d1.item_no = #{itemNo}
and d1.document_type in
#{documentType}

SELECT u.depart_id FROM document d1 INNER JOIN user u ON d1.user_id = u.id where d1.id in( select max(d2.id) from document d2 group by d2.item_no, d2.material_no, d2.document_type, d2.sequence_no )
and d1.item_no = #{itemNo}
and d1.material_no = #{materialNo}
LIMIT 1


WHERE d1.item_no = #{itemNo}
AND d1.sequence_no = #{sequenceNo}
AND d1.document_type = #{documentType}
AND d1.document_version <> #{documentVersion}

    AND d1.material_no IS NULL


    AND d1.material_no = #{materialNo}


    AND d1.approval_status = 1


    AND d1.publish_status = 1


    AND d1.deleted = 0

order by d1.create_time desc
limit 1
